function run(response) {
  const json = JSON.parse(response);

  const values = json.map((item) => {
    return `{
            "header": {
            "type": "application/vnd.lime.media-link+json",
            "value": {
              "title": "${item.name}",
              "text": "${item.description}",
              "type": "image/png",
              "uri": "${item.avatar}"
            }
          }
        }`;
  });

  const content =
    '{ "itemType": "application/vnd.lime.document-select+json", "items": [' +
    values +
    ']}';

  return content;
}
